define({"topics" : [{"title":"Azure Synapse Connection","shortdesc":"\n                  <p class=\"shortdesc\">The Azure Synapse SQL destination requires connection details to connect to your         Azure Synapse instance. </p>\n               ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_vbw_wfg_mjb","attributes": {"data-id":"concept_vbw_wfg_mjb",},"menu": {"hasChildren":false,},"tocID":"concept_vbw_wfg_mjb-d46e143004","topics":[]},{"title":"Staging Connection","shortdesc":"\n                  <p class=\"shortdesc\">The Azure Synapse SQL destination requires connection details to connect to the         storage account that stages pipeline\n                     data. You can use Azure Blob Storage or Azure Data Lake         Gen2 as the staging area.\n                  </p>\n               ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_jqb_1gg_mjb","attributes": {"data-id":"concept_jqb_1gg_mjb",},"menu": {"hasChildren":false,},"tocID":"concept_jqb_1gg_mjb-d46e143110","topics":[]},{"title":"Copy Statement Connection","shortdesc":"\n                  <p class=\"shortdesc\">The Azure Synapse SQL destination creates a connection to issue a <a class=\"xref\" href=\"datacollector\/UserGuide\/Destinations\/https:\/\/docs.microsoft.com\/en-us\/sql\/t-sql\/statements\/copy-into-transact-sql?view=azure-sqldw-latest\" target=\"_blank\">COPY command</a> that reads from the staging area and         bulk loads the data into Azure Synapse. \n                  </p>\n               ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_trh_j3v_1nb","attributes": {"data-id":"concept_trh_j3v_1nb",},"menu": {"hasChildren":false,},"tocID":"concept_trh_j3v_1nb-d46e143226","topics":[]}]});