define({"topics" : [{"title":"Prerequisites","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_z1h_3jf_mjb","attributes": {"data-id":"concept_z1h_3jf_mjb",},"menu": {"hasChildren":true,},"tocID":"concept_z1h_3jf_mjb-d46e142403","next":"concept_z1h_3jf_mjb-d46e142403",},{"title":"Connections and Authentication","shortdesc":"\n               <p class=\"shortdesc\"></p>\n            ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_hjn_fxk_4jb","attributes": {"data-id":"concept_hjn_fxk_4jb",},"menu": {"hasChildren":true,},"tocID":"concept_hjn_fxk_4jb-d46e142869","next":"concept_hjn_fxk_4jb-d46e142869",},{"title":"Specifying Tables","shortdesc":"\n               <p class=\"shortdesc\">You can use the Azure Synapse SQL destination to write to one or more tables in a         schema. The Azure Synapse SQL destination\n                  can load data into a single existing         schema.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_rcw_dbc_njb","attributes": {"data-id":"concept_rcw_dbc_njb",},"menu": {"hasChildren":false,},"tocID":"concept_rcw_dbc_njb-d46e143355","topics":[]},{"title":"Enabling Data Drift Handling","shortdesc":"\n               <p class=\"shortdesc\">The Azure Synapse SQL destination can automatically compensate for changes in column         or table requirements, also known\n                  as <em class=\"ph i\">data drift</em>.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_tbb_hbc_njb","attributes": {"data-id":"concept_tbb_hbc_njb",},"menu": {"hasChildren":false,},"tocID":"concept_tbb_hbc_njb-d46e143501","topics":[]},{"title":"Row Generation","shortdesc":"\n               <p class=\"shortdesc\">When loading a record to a table, the Azure Synapse SQL destination includes all         record fields in the resulting row,\n                  by default. The destination uses the root field,             <code class=\"ph codeph\">\/</code>, as the basis for the resulting row.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_upt_1bc_njb","attributes": {"data-id":"concept_upt_1bc_njb",},"menu": {"hasChildren":true,},"tocID":"concept_upt_1bc_njb-d46e143660","next":"concept_upt_1bc_njb-d46e143660",},{"title":"Performance Optimization","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_yz1_pvb_njb","attributes": {"data-id":"concept_yz1_pvb_njb",},"menu": {"hasChildren":false,},"tocID":"concept_yz1_pvb_njb-d46e144006","topics":[]},{"title":"Azure Synapse Data Types","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#concept_zjk_lbc_njb","attributes": {"data-id":"concept_zjk_lbc_njb",},"menu": {"hasChildren":false,},"tocID":"concept_zjk_lbc_njb-d46e144189","topics":[]},{"title":"Configuring an Azure Synapse SQL Destination","href":"datacollector\/UserGuide\/Destinations\/AzureSynapse.html#task_kkh_p4n_r3b","attributes": {"data-id":"task_kkh_p4n_r3b",},"menu": {"hasChildren":false,},"tocID":"task_kkh_p4n_r3b-d46e144383","topics":[]}]});