define({"topics" : [{"title":"Local Keystore and Truststore","shortdesc":"\n                  <p class=\"shortdesc\">You can configure stages to load the keystore or truststore from a local file stored         on the <span class=\"ph\">Data Collector</span><span class=\"ph\"> or <span class=\"ph\">Data Collector Edge</span></span> machine. \n                  </p>\n               ","href":"datacollector\/UserGuide\/Pipeline_Configuration\/SSL-TLS.html#concept_qdt_jj2_1nb","attributes": {"data-id":"concept_qdt_jj2_1nb",},"menu": {"hasChildren":false,},"tocID":"concept_qdt_jj2_1nb-d46e41619","topics":[]},{"title":"Remote Keystore and Truststore","shortdesc":"\n                  <p class=\"shortdesc\">You can configure stages to load the contents of the keystore or truststore from a         remote credential store or from\n                     values entered in the stage properties. The stage builds the         keystore or truststore from the private key and certificates\n                     retrieved from the credential         store or entered in the stage properties.\n                  </p>\n               ","href":"datacollector\/UserGuide\/Pipeline_Configuration\/SSL-TLS.html#concept_q2c_hj2_1nb","attributes": {"data-id":"concept_q2c_hj2_1nb",},"menu": {"hasChildren":true,},"tocID":"concept_q2c_hj2_1nb-d46e41651","next":"concept_q2c_hj2_1nb-d46e41651",}]});