define({"topics" : [{"title":"Using a Credential Store","shortdesc":"\n                     <p class=\"shortdesc\">You can define keystore\/truststore private keys and certificates in a credential         store, then call the appropriate\n                        key and certificate from a stage that uses SSL\/TLS         encryption. The stage builds the keystore or truststore from the\n                        specified private key and         certificates.\n                     </p>\n                  ","href":"datacollector\/UserGuide\/Pipeline_Configuration\/SSL-TLS.html#concept_ahd_fp2_1nb","attributes": {"data-id":"concept_ahd_fp2_1nb",},"menu": {"hasChildren":false,},"tocID":"concept_ahd_fp2_1nb-d46e41696","topics":[]}]});